(115, 38, 0, 'Database Relationships', 'database-relationships', '<p id=\"bkmrk-\"><a href=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/realtionships.png\" target=\"_blank\" rel=\"noopener\"><img src=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/scaled-1680-/realtionships.png\" alt=\"realtionships.png\"></a></p>\r\n<p id=\"bkmrk-%C2%A0\">&nbsp;</p>\r\n<pre id=\"bkmrk-%23-creating-database-\"><code class=\"language-sql\"># Creating Database\r\ncreate database employee_management;\r\n\r\n\r\n# Use database\r\nuse employee_management;\r\n\r\n# Creating Tables\r\n\r\n## Order of Tables\r\n \r\n### job (id, type, name, created, updated)\r\nCREATE TABLE job (\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  type VARCHAR(100) DEFAULT NULL,\r\n  name VARCHAR(200) NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id)\r\n);\r\n\r\n### contact_info (id, phone, email, created, updated)\r\nCREATE TABLE contact_info(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  phone INT(20) DEFAULT NULL,\r\n  email VARCHAR(50) NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id)\r\n);\r\n\r\n### project (id, created, title, priority, updated)\r\n\r\nCREATE TABLE project(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  title VARCHAR(200) NOT NULL,\r\n  priority ENUM(\'High\', \'Medium\', \'Low\', \'None\') DEFAULT \'None\',\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id)\r\n);\r\n\r\n### state (id, created, name, code, updated)\r\nCREATE TABLE state(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  name VARCHAR(100) NOT NULL,\r\n  code VARCHAR(5) NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id)\r\n);\r\n\r\n\r\n### address (id, created, address_line_1, address_line_2, city, state_id, pincode, updated)\r\nCREATE TABLE address(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  address_line_1 VARCHAR(200) NOT NULL,\r\n  address_line_2 VARCHAR(200) DEFAULT NULL,\r\n  city VARCHAR(50) NOT NULL,\r\n  state_id INT NOT NULL,\r\n  pincode INT(10) NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id),\r\n  FOREIGN KEY (state_id) REFERENCES state(id)\r\n);\r\n\r\n\r\n### employee (id, created, first_name, last_name, contact_info_id, job_id, address_id, updated)\r\nCREATE TABLE employee(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  first_name VARCHAR(200) NOT NULL,\r\n  last_name VARCHAR(200) NOT NULL,\r\n  contact_info_id INT NOT NULL,\r\n  job_id INT NOT NULL,\r\n  address_id INT NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id),\r\n  FOREIGN KEY (contact_info_id) REFERENCES contact_info(id),\r\n  FOREIGN KEY (job_id) REFERENCES job(id),\r\n  FOREIGN KEY (address_id) REFERENCES address(id),\r\n  UNIQUE (contact_info_id)\r\n);\r\n\r\n### employee_project (id, employee_id, project_id, created, updated)\r\nCREATE TABLE employee_project(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  employee_id INT NOT NULL,\r\n  project_id INT NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id),\r\n  FOREIGN KEY (employee_id) REFERENCES employee(id),\r\n  FOREIGN KEY (project_id) REFERENCES project(id)\r\n);</code></pre>\r\n<p id=\"bkmrk-%C2%A0-1\">&nbsp;</p>', '\r\n \r\n# Creating Database\r\ncreate database employee_management;\r\n\r\n\r\n# Use database\r\nuse employee_management;\r\n\r\n# Creating Tables\r\n\r\n## Order of Tables\r\n \r\n### job (id, type, name, created, updated)\r\nCREATE TABLE job (\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  type VARCHAR(100) DEFAULT NULL,\r\n  name VARCHAR(200) NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id)\r\n);\r\n\r\n### contact_info (id, phone, email, created, updated)\r\nCREATE TABLE contact_info(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  phone INT(20) DEFAULT NULL,\r\n  email VARCHAR(50) NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id)\r\n);\r\n\r\n### project (id, created, title, priority, updated)\r\n\r\nCREATE TABLE project(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  title VARCHAR(200) NOT NULL,\r\n  priority ENUM(\'High\', \'Medium\', \'Low\', \'None\') DEFAULT \'None\',\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id)\r\n);\r\n\r\n### state (id, created, name, code, updated)\r\nCREATE TABLE state(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  name VARCHAR(100) NOT NULL,\r\n  code VARCHAR(5) NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id)\r\n);\r\n\r\n\r\n### address (id, created, address_line_1, address_line_2, city, state_id, pincode, updated)\r\nCREATE TABLE address(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  address_line_1 VARCHAR(200) NOT NULL,\r\n  address_line_2 VARCHAR(200) DEFAULT NULL,\r\n  city VARCHAR(50) NOT NULL,\r\n  state_id INT NOT NULL,\r\n  pincode INT(10) NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id),\r\n  FOREIGN KEY (state_id) REFERENCES state(id)\r\n);\r\n\r\n\r\n### employee (id, created, first_name, last_name, contact_info_id, job_id, address_id, updated)\r\nCREATE TABLE employee(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  first_name VARCHAR(200) NOT NULL,\r\n  last_name VARCHAR(200) NOT NULL,\r\n  contact_info_id INT NOT NULL,\r\n  job_id INT NOT NULL,\r\n  address_id INT NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id),\r\n  FOREIGN KEY (contact_info_id) REFERENCES contact_info(id),\r\n  FOREIGN KEY (job_id) REFERENCES job(id),\r\n  FOREIGN KEY (address_id) REFERENCES address(id),\r\n  UNIQUE (contact_info_id)\r\n);\r\n\r\n### employee_project (id, employee_id, project_id, created, updated)\r\nCREATE TABLE employee_project(\r\n	id INT AUTO_INCREMENT NOT NULL,\r\n  employee_id INT NOT NULL,\r\n  project_id INT NOT NULL,\r\n  created DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, \r\n  updated DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL,\r\n  PRIMARY KEY(id),\r\n  FOREIGN KEY (employee_id) REFERENCES employee(id),\r\n  FOREIGN KEY (project_id) REFERENCES project(id)\r\n);\r\n ', 6, '2024-03-07 14:08:16', '2024-03-08 14:23:38', 1, 1, 0, '', 3, 0, NULL, 1, ''),
