(564, 117, 'Retrieving Data', '<h4 id=\"bkmrk-queries\">Queries</h4>\r\n<pre id=\"bkmrk-%23-select-select-%2A-fr\"><code class=\"language-sql\"># Select\r\nSELECT * FROM employee;\r\n\r\nSELECT id, first_name, contact_info_id FROM employee;\r\n\r\nSELECT * FROM employee WHERE id &lt;&gt; 10;\r\n\r\nSELECT * FROM employee WHERE id &lt;&gt; 10 AND job_id &gt; 2;\r\n\r\nSELECT * FROM employee WHERE id &lt;&gt; 10 ORDER BY first_name DESC;\r\n\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email, \r\n  j.type AS job_type, j.name AS job_name,\r\n  addr.address_line_1, addr.city,\r\n  s.name AS state_name, addr.pincode\r\nFROM employee emp \r\nINNER JOIN contact_info ci ON emp.contact_info_id = ci.id \r\nINNER JOIN job j ON emp.job_id = j.id\r\nINNER JOIN address addr ON emp.address_id = addr.id\r\nINNER JOIN state s ON addr.state_id = s.id\r\nWHERE emp.first_name = \'John\';\r\n\r\n# FULL OUTER JOIN\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email\r\nFROM employee emp\r\nLEFT JOIN contact_info ci ON emp.contact_info_id = ci.id\r\nUNION\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email\r\nFROM employee emp\r\nRIGHT JOIN contact_info ci ON emp.contact_info_id = ci.id;\r\n</code></pre>\r\n<p id=\"bkmrk-%C2%A0\">&nbsp;</p>\r\n<h4 id=\"bkmrk-how-joins-works-in-d\"><br>How JOINS works in Database?</h4>\r\n<p id=\"bkmrk-\"><a href=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/tFsimage.png\" target=\"_blank\" rel=\"noopener\"><img src=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/scaled-1680-/tFsimage.png\" alt=\"image.png\"></a></p>\r\n<p id=\"bkmrk--1\"></p>\r\n<p id=\"bkmrk--2\"><a href=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/3L2image.png\" target=\"_blank\" rel=\"noopener\"><img src=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/scaled-1680-/3L2image.png\" alt=\"image.png\"></a></p>\r\n<h4 id=\"bkmrk-sql-execution-order\">SQL Execution Order</h4>\r\n<p id=\"bkmrk--3\"><a href=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/RiMimage.png\" target=\"_blank\" rel=\"noopener\"><img src=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/scaled-1680-/RiMimage.png\" alt=\"image.png\"></a></p>\r\n<p id=\"bkmrk--4\"></p>\r\n<p id=\"bkmrk--5\"><br></p>', 'Queries\r\n# Select\r\nSELECT * FROM employee;\r\n\r\nSELECT id, first_name, contact_info_id FROM employee;\r\n\r\nSELECT * FROM employee WHERE id <> 10;\r\n\r\nSELECT * FROM employee WHERE id <> 10 AND job_id > 2;\r\n\r\nSELECT * FROM employee WHERE id <> 10 ORDER BY first_name DESC;\r\n\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email, \r\n  j.type AS job_type, j.name AS job_name,\r\n  addr.address_line_1, addr.city,\r\n  s.name AS state_name, addr.pincode\r\nFROM employee emp \r\nINNER JOIN contact_info ci ON emp.contact_info_id = ci.id \r\nINNER JOIN job j ON emp.job_id = j.id\r\nINNER JOIN address addr ON emp.address_id = addr.id\r\nINNER JOIN state s ON addr.state_id = s.id\r\nWHERE emp.first_name = \'John\';\r\n\r\n# FULL OUTER JOIN\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email\r\nFROM employee emp\r\nLEFT JOIN contact_info ci ON emp.contact_info_id = ci.id\r\nUNION\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email\r\nFROM employee emp\r\nRIGHT JOIN contact_info ci ON emp.contact_info_id = ci.id;\r\n\r\n \r\nHow JOINS works in Database?\r\n\r\n\r\n\r\nSQL Execution Order\r\n\r\n\r\n', 1, '2024-03-12 12:33:31', '2024-03-12 12:33:31', 'retrieving-data', 'databases', 'version', '', '', 4),
(565, 118, 'Updating Table Data and Structure', '<pre id=\"bkmrk-%23-update-update-addr\"><code class=\"language-\"> # Update\r\nUPDATE\r\n  address\r\nSET\r\n  address_line_1 = \'123 Main street Avenue\',\r\n  address_line_2 = \'Suite #123\'\r\nWHERE\r\n  pincode = 90001;\r\n\r\nUPDATE\r\n  address\r\nSET\r\n  address_line_2 = (SELECT email FROM contact_info WHERE phone = \'7773331111\')\r\nWHERE\r\n  pincode = 30040;\r\n\r\n# Alter\r\nALTER TABLE state\r\nADD tagline VARCHAR(100) DEFAULT NULL;\r\n\r\nALTER TABLE state\r\nMODIFY tagline TEXT DEFAULT NULL;\r\n\r\nALTER TABLE state\r\nCHANGE COLUMN tagline tag_line TEXT DEFAULT NULL\r\nAFTER code;\r\n\r\nALTER TABLE state\r\nDROP COLUMN tag_line;\r\n\r\n\r\nALTER TABLE address\r\nADD CONSTRAINT fk_state_id\r\nFOREIGN KEY (state_id)\r\nREFERENCES state(id);\r\n\r\nALTER TABLE states\r\nRENAME to state;\r\n</code></pre>\r\n<p id=\"bkmrk-%C2%A0\">&nbsp;</p>', ' # Update\r\nUPDATE\r\n  address\r\nSET\r\n  address_line_1 = \'123 Main street Avenue\',\r\n  address_line_2 = \'Suite #123\'\r\nWHERE\r\n  pincode = 90001;\r\n\r\nUPDATE\r\n  address\r\nSET\r\n  address_line_2 = (SELECT email FROM contact_info WHERE phone = \'7773331111\')\r\nWHERE\r\n  pincode = 30040;\r\n\r\n# Alter\r\nALTER TABLE state\r\nADD tagline VARCHAR(100) DEFAULT NULL;\r\n\r\nALTER TABLE state\r\nMODIFY tagline TEXT DEFAULT NULL;\r\n\r\nALTER TABLE state\r\nCHANGE COLUMN tagline tag_line TEXT DEFAULT NULL\r\nAFTER code;\r\n\r\nALTER TABLE state\r\nDROP COLUMN tag_line;\r\n\r\n\r\nALTER TABLE address\r\nADD CONSTRAINT fk_state_id\r\nFOREIGN KEY (state_id)\r\nREFERENCES state(id);\r\n\r\nALTER TABLE states\r\nRENAME to state;\r\n\r\n ', 1, '2024-03-13 13:23:32', '2024-03-13 13:23:32', 'updating-table-data-and-structure', 'databases', 'version', '', 'Initial publish', 1),
(566, 117, 'Retrieving Data', '<h4 id=\"bkmrk-queries\">Queries</h4>\r\n<pre id=\"bkmrk-%23-select-select-%2A-fr\"><code class=\"language-sql\"># Select\r\nSELECT * FROM employee;\r\n\r\nSELECT id, first_name, contact_info_id FROM employee;\r\n\r\nSELECT * FROM employee WHERE id &lt;&gt; 10;\r\n\r\nSELECT * FROM employee WHERE id &lt;&gt; 10 AND job_id &gt; 2;\r\n\r\nSELECT * FROM employee WHERE id &lt;&gt; 10 ORDER BY first_name DESC;\r\n\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email, \r\n  j.type AS job_type, j.name AS job_name,\r\n  addr.address_line_1, addr.city,\r\n  s.name AS state_name, addr.pincode\r\nFROM employee emp \r\nINNER JOIN contact_info ci ON emp.contact_info_id = ci.id \r\nINNER JOIN job j ON emp.job_id = j.id\r\nINNER JOIN address addr ON emp.address_id = addr.id\r\nINNER JOIN state s ON addr.state_id = s.id\r\nWHERE emp.first_name = \'John\';\r\n\r\n# FULL OUTER JOIN\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email\r\nFROM employee emp\r\nLEFT JOIN contact_info ci ON emp.contact_info_id = ci.id\r\nUNION\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email\r\nFROM employee emp\r\nRIGHT JOIN contact_info ci ON emp.contact_info_id = ci.id;\r\n\r\n# Using LIKE and LIMIT\r\nSELECT\r\n  customerNumber,\r\n  contactFirstName\r\nFROM\r\n  customers\r\nWHERE\r\n  contactFirstName LIKE \'%e%\'\r\n  OR contactFirstName LIKE \'%d%\'\r\nLIMIT\r\n  10;\r\n\r\n\r\nSELECT\r\n  customerNumber,\r\n  contactFirstName\r\nFROM\r\n  customers\r\nWHERE\r\n  contactFirstName IN (\'Jean\', \'Peter\');\r\n\r\nSELECT\r\n  customerNumber,\r\n  contactFirstName,\r\n  addressLine2\r\nFROM\r\n  customers\r\nWHERE\r\n  addressLine2 IS NULL;\r\n\r\n\r\nSELECT\r\n  customerName,\r\n  phone,\r\n  city\r\nfrom\r\n  customers\r\nORDER BY\r\n  city DESC;\r\n\r\n\r\nSELECT\r\n  city,\r\n  count(customerNumber) AS CustomerCount\r\nFROM\r\n  customers\r\nWHERE\r\n  contactFirstName LIKE \'%ea%\'\r\nGROUP BY\r\n  city \r\nHAVING CustomerCount &gt;=1;\r\n</code></pre>\r\n<p id=\"bkmrk-%C2%A0\"></p>\r\n<h4 id=\"bkmrk-how-joins-works-in-d\"><br>How JOINS works in Database?</h4>\r\n<p id=\"bkmrk-\"><a href=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/tFsimage.png\" target=\"_blank\" rel=\"noopener\"><img src=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/scaled-1680-/tFsimage.png\" alt=\"image.png\"></a></p>\r\n<p id=\"bkmrk--1\"></p>\r\n<p id=\"bkmrk--2\"><a href=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/3L2image.png\" target=\"_blank\" rel=\"noopener\"><img src=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/scaled-1680-/3L2image.png\" alt=\"image.png\"></a></p>\r\n<h4 id=\"bkmrk-sql-execution-order\">SQL Execution Order</h4>\r\n<p id=\"bkmrk--3\"><a href=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/RiMimage.png\" target=\"_blank\" rel=\"noopener\"><img src=\"https://docs.weintegral.com/uploads/images/gallery/2024-03/scaled-1680-/RiMimage.png\" alt=\"image.png\"></a></p>\r\n<p id=\"bkmrk--4\"></p>\r\n<p id=\"bkmrk--5\"><br></p>', 'Queries\r\n# Select\r\nSELECT * FROM employee;\r\n\r\nSELECT id, first_name, contact_info_id FROM employee;\r\n\r\nSELECT * FROM employee WHERE id <> 10;\r\n\r\nSELECT * FROM employee WHERE id <> 10 AND job_id > 2;\r\n\r\nSELECT * FROM employee WHERE id <> 10 ORDER BY first_name DESC;\r\n\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email, \r\n  j.type AS job_type, j.name AS job_name,\r\n  addr.address_line_1, addr.city,\r\n  s.name AS state_name, addr.pincode\r\nFROM employee emp \r\nINNER JOIN contact_info ci ON emp.contact_info_id = ci.id \r\nINNER JOIN job j ON emp.job_id = j.id\r\nINNER JOIN address addr ON emp.address_id = addr.id\r\nINNER JOIN state s ON addr.state_id = s.id\r\nWHERE emp.first_name = \'John\';\r\n\r\n# FULL OUTER JOIN\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email\r\nFROM employee emp\r\nLEFT JOIN contact_info ci ON emp.contact_info_id = ci.id\r\nUNION\r\nSELECT \r\n	emp.first_name, emp.last_name, \r\n  ci.phone, ci.email\r\nFROM employee emp\r\nRIGHT JOIN contact_info ci ON emp.contact_info_id = ci.id;\r\n\r\n# Using LIKE and LIMIT\r\nSELECT\r\n  customerNumber,\r\n  contactFirstName\r\nFROM\r\n  customers\r\nWHERE\r\n  contactFirstName LIKE \'%e%\'\r\n  OR contactFirstName LIKE \'%d%\'\r\nLIMIT\r\n  10;\r\n\r\n\r\nSELECT\r\n  customerNumber,\r\n  contactFirstName\r\nFROM\r\n  customers\r\nWHERE\r\n  contactFirstName IN (\'Jean\', \'Peter\');\r\n\r\nSELECT\r\n  customerNumber,\r\n  contactFirstName,\r\n  addressLine2\r\nFROM\r\n  customers\r\nWHERE\r\n  addressLine2 IS NULL;\r\n\r\n\r\nSELECT\r\n  customerName,\r\n  phone,\r\n  city\r\nfrom\r\n  customers\r\nORDER BY\r\n  city DESC;\r\n\r\n\r\nSELECT\r\n  city,\r\n  count(customerNumber) AS CustomerCount\r\nFROM\r\n  customers\r\nWHERE\r\n  contactFirstName LIKE \'%ea%\'\r\nGROUP BY\r\n  city \r\nHAVING CustomerCount >=1;\r\n\r\n\r\nHow JOINS works in Database?\r\n\r\n\r\n\r\nSQL Execution Order\r\n\r\n\r\n', 1, '2024-03-13 13:24:00', '2024-03-13 13:24:00', 'retrieving-data', 'databases', 'version', '', '', 5),
(567, 119, 'Stored Procedures', '<pre id=\"bkmrk-create-procedure-get\"><code class=\"language-sql\">CREATE PROCEDURE GetEmployees()\r\nBEGIN\r\n	SELECT * FROM employee;\r\nEND;\r\n\r\n\r\nCALL GetEmployees();\r\n\r\nDROP PROCEDURE GetEmployees;\r\n\r\n\r\nCREATE PROCEDURE GetEmployeeByFirstName(IN first_name VARCHAR(200))\r\nBEGIN\r\n	SELECT\r\n    emp.first_name, emp.last_name, \r\n    ci.phone, ci.email, \r\n    j.type AS job_type, j.name AS job_name,\r\n    addr.address_line_1, addr.city,\r\n    s.name AS state_name, addr.pincode\r\n  FROM employee emp \r\n  INNER JOIN contact_info ci ON emp.contact_info_id = ci.id \r\n  INNER JOIN job j ON emp.job_id = j.id\r\n  INNER JOIN address addr ON emp.address_id = addr.id\r\n  INNER JOIN state s ON addr.state_id = s.id\r\n  WHERE emp.first_name = first_name;\r\nEND;\r\n\r\n\r\nCALL GetEmployeeByFirstName(\'John\');\r\n\r\nDROP PROCEDURE GetEmployeeByFirstName;\r\n\r\nCREATE PROCEDURE GetEmployeeById(IN emp_id INT, OUT total INT)\r\nBEGIN\r\n	SELECT\r\n    count(emp.id) INTO total\r\n  FROM employee emp \r\n  INNER JOIN contact_info ci ON emp.contact_info_id = ci.id \r\n  INNER JOIN job j ON emp.job_id = j.id\r\n  INNER JOIN address addr ON emp.address_id = addr.id\r\n  INNER JOIN state s ON addr.state_id = s.id\r\n  WHERE emp.id = emp_id;\r\nEND;\r\n\r\nCALL GetEmployeeById(10, @total);\r\nSELECT @total;\r\n\r\nDROP PROCEDURE GetEmployeeById;</code></pre>\r\n<p id=\"bkmrk-%C2%A0\">&nbsp;</p>', 'CREATE PROCEDURE GetEmployees()\r\nBEGIN\r\n	SELECT * FROM employee;\r\nEND;\r\n\r\n\r\nCALL GetEmployees();\r\n\r\nDROP PROCEDURE GetEmployees;\r\n\r\n\r\nCREATE PROCEDURE GetEmployeeByFirstName(IN first_name VARCHAR(200))\r\nBEGIN\r\n	SELECT\r\n    emp.first_name, emp.last_name, \r\n    ci.phone, ci.email, \r\n    j.type AS job_type, j.name AS job_name,\r\n    addr.address_line_1, addr.city,\r\n    s.name AS state_name, addr.pincode\r\n  FROM employee emp \r\n  INNER JOIN contact_info ci ON emp.contact_info_id = ci.id \r\n  INNER JOIN job j ON emp.job_id = j.id\r\n  INNER JOIN address addr ON emp.address_id = addr.id\r\n  INNER JOIN state s ON addr.state_id = s.id\r\n  WHERE emp.first_name = first_name;\r\nEND;\r\n\r\n\r\nCALL GetEmployeeByFirstName(\'John\');\r\n\r\nDROP PROCEDURE GetEmployeeByFirstName;\r\n\r\nCREATE PROCEDURE GetEmployeeById(IN emp_id INT, OUT total INT)\r\nBEGIN\r\n	SELECT\r\n    count(emp.id) INTO total\r\n  FROM employee emp \r\n  INNER JOIN contact_info ci ON emp.contact_info_id = ci.id \r\n  INNER JOIN job j ON emp.job_id = j.id\r\n  INNER JOIN address addr ON emp.address_id = addr.id\r\n  INNER JOIN state s ON addr.state_id = s.id\r\n  WHERE emp.id = emp_id;\r\nEND;\r\n\r\nCALL GetEmployeeById(10, @total);\r\nSELECT @total;\r\n\r\nDROP PROCEDURE GetEmployeeById;\r\n ', 1, '2024-03-14 12:22:13', '2024-03-14 12:22:13', 'stored-procedures', 'databases', 'version', '', 'Initial publish', 1),
